// Post-login script: Extract JWT from Set-Cookie header and save to environment
// This runs after the login request completes successfully

// Only process if login was successful
if (pm.response.code === 200 || pm.response.code === 201) {
  // Get the Set-Cookie header(s)
  const setCookieHeader = pm.response.headers.get("Set-Cookie");

  if (setCookieHeader) {
    // Parse the access_token from the cookie
    // Cookie format: access_token=<jwt>; Path=/; HttpOnly; ...
    const cookies = setCookieHeader.split(",").map((c) => c.trim());

    for (const cookie of cookies) {
      const tokenMatch = cookie.match(/access_token=([^;]+)/);
      if (tokenMatch && tokenMatch[1]) {
        const token = tokenMatch[1];

        // Save token to environment variable
        pm.environment.set("bearerToken", token);
        console.log("Token saved to bearerToken environment variable");

        // Optionally decode and save expiry
        try {
          const parts = token.split(".");
          if (parts.length === 3) {
            const payload = JSON.parse(atob(parts[1]));
            if (payload.exp) {
              pm.environment.set("token_expiry", payload.exp.toString());
              console.log(
                "Token expiry saved:",
                new Date(payload.exp * 1000).toISOString(),
              );
            }
          }
        } catch (e) {
          console.log("Could not decode token expiry:", e.message);
        }

        break;
      }
    }
  } else {
    console.log("No Set-Cookie header found in response");
  }
}

// Pre-request script: Check JWT validity before making authenticated requests
// Runs before EVERY request in the collection

// Public endpoints that don't need authentication
const publicEndpoints = [
  "/api/auth/login",
  "/api/auth/register",
  "/api/auth/logout",
  "/api/health",
];

// Get the current request path
const requestPath = pm.request.url.getPath();

// Check if this is a public endpoint
const isPublic = publicEndpoints.some(
  (endpoint) => requestPath === endpoint || requestPath.startsWith(endpoint),
);

if (!isPublic) {
  // Check if we have a token
  const token = pm.environment.get("bearerToken");
  const expiry = pm.environment.get("token_expiry");

  if (!token) {
    console.warn(
      "No bearerToken found. Run login request first to authenticate.",
    );
  } else if (expiry) {
    // Check if token is expired
    const now = Math.floor(Date.now() / 1000);
    const expiryTime = parseInt(expiry, 10);

    if (now >= expiryTime) {
      console.warn("Token has expired. Please login again.");
      // Optionally clear the expired token
      // pm.environment.set('bearerToken', '');
      // pm.environment.set('token_expiry', '');
    } else {
      const remainingMinutes = Math.floor((expiryTime - now) / 60);
      if (remainingMinutes < 5) {
        console.log(
          `Token expires in ${remainingMinutes} minutes. Consider refreshing.`,
        );
      }
    }
  }
}
/**
 * Strip JSON Comments - Collection Pre-Request Script
 *
 * This script strips JS-style comments from JSON request bodies before sending.
 * Supports both line comments (//) and block comments.
 * This allows optional fields to be commented out in the Postman collection
 * while still being valid when uncommented.
 *
 * Usage: Optional fields are shown commented out like:
 *   {
 *     "name": "Test Agent {{$timestamp}}",
 *     "model": "openai/gpt-4o-mini"
 *     // "description": "<string>",
 *     // "temperature": 0.7,
 *   }
 *
 * When you uncomment a field, it will be included in the request.
 */

// Regex to strip JS comments while preserving strings
// Matches: quoted strings (to skip) OR // line comments OR /* block comments */
const stripComments = (jsonBody) =>
  jsonBody.replace(
    /\\"|"(?:\\"|[^"])*"|(\/\/.*|\/\*[\s\S]*?\*\/)/g,
    (match, group) => (group ? '' : match),
  );

// Only process if there's a raw body
if (pm.request.body && pm.request.body.raw) {
  const original = pm.request.body.raw;
  const cleaned = stripComments(original);

  // Only modify if comments were found
  if (cleaned !== original) {
    // Also clean up any trailing commas before closing braces
    // (in case the last required field had a comma for the commented fields)
    const finalCleaned = cleaned.replace(/,(\s*[}\]])/g, '$1');

    pm.request.body.raw = finalCleaned;
    console.log('Stripped comments from request body');
  }
}
